## Model 6: Spatial Heterogeneity via Mixed Effects


::: {.cell layout-align="center"}

```{.r .cell-code}
re_data <- select_model_features(df_final, keep_location = TRUE) %>%
  scale_data()

ctrl <- glmmTMBControl(
  optimizer = optim,
  optArgs = list(method = "BFGS"),
  optCtrl = list(maxit = 1000)
)

m6_mixed <- glmmTMB(
  rainfall ~
    humidity3pm +
    dewpoint_9am +
    dewpoint_change +
    pressure_change +
    day_cos +
    day_sin +
    rainfall_ma7 +
    days_since_rain +
    humidity_ma7 +
    rain_yesterday +
    sunshine +
    evaporation +
    instability_index +
    sun_humid_interaction +
    cloud_development +
    gust_U_EW +
    gust_V_NS +
    wind9am_V_NS +
    wind9am_U_EW +
    diag(1 + humidity3pm + rain_yesterday + dewpoint_change | location),
  ziformula = ~ humidity3pm +
    dewpoint_9am +
    rain_yesterday +
    cloud_development +
    pressure_change +
    sunshine +
    evaporation +
    ns(days_since_rain, df = 4) +
    humidity_ma7 +
    day_cos +
    day_sin,
  data = re_data,
  control = ctrl,
  family = ziGamma(link = "log")
)
